import React, { useState } from 'react';
import { EmptyState, Layout, Modal, Page } from '@shopify/polaris';
import { ResourcePicker, TitleBar } from '@shopify/app-bridge-react';
import store from 'store-js';
import ProductList from '../components/ProductList';
import axios from 'axios';

const Index = () => {
    const [modal, setModal] = useState({open: false});
    const emptyState = !store.get('ids');

    function handleSection(resources){
        const idsFromResources = resources.selection.map((product) => product.id);
        setModal({open: false});
        store.set('ids', idsFromResources)
        console.log('this is product ids', store.get('ids'))
    }

    return (
        <Page>
            <TitleBar
                primaryAction={{
                    content: 'Select New Products',
                    onAction: () => setModal({ open: true })
                }}
            />
            <ResourcePicker 
                resourceType="Product"
                showVariants={false}
                open={modal.open}
                onCancel={() => setModal({open: false})}
                onSelection={(resources) => handleSection(resources)}
            />
            
                { emptyState 
                    ? (
                        <Layout>
                            <EmptyState
                                heading="Manage your inventory transfers"
                                action={{
                                    content: 'Select Products',
                                    onAction: () => setModal({ open: true })
                                }}
                                image="https://cdn.shopify.com/s/files/1/0757/9955/files/empty-state.svg"
                            >
                                <p>Select Products</p>
                            </EmptyState>
                        </Layout>
                      )
                    : (<ProductList/>)
                }
        </Page>
    )
}

export default Index
