import gql from 'graphql-tag';
import {useQuery, useMutation} from '@apollo/react-hooks';

const CREATE_SCRIPT_TAG = gql`
    mutation scriptTagCreate($input: ScriptTagInput!){
        scriptTagCreate(input: $input){
            scriptTag{
                id
            }
            userErrors{
                field
                message
            }
        }
    }
`;

const QUERY_SCRIPT_TAGS = gql`
    query{
        scriptTags(first: 5){
            edges{
                node{
                    id
                    src
                    displayScope
                }
            }
        }
    }
`;

const DELETE_SCRIPT_TAG = gql`
    mutation scriptTagDelete($idL ID!){
        scriptTagDelete(id: $id){
            deletedScriptTagId
            userErrors{
                field
                message
            }
        }
    }
`;


const ScriptPage = () => {

    const[createScripts] = useMutation(CREATE_SCRIPT_TAG);
    const {loading, error, data } = useQuery(QUERY_SCRIPT_TAGS);

    if (loading) return <div>Loading...</div>;
    if (error) return <div>{error.message}</div>;

    console.log('this is data:', data)

    return (
        <div>
            <h1>These are the script tags right now:</h1>
            <button
                type='submit' 
                onClick={() => {
                    createScripts({ variables: {input: {src: "https://a30dd80893d0.ngrok.io/test-script.js", displayScope: "ALL"}}})
                }}
            >Create Scrip Tag</button>

            {data.scriptTags.edges.map(item => {
                return(
                    <div key={item.node.id}>
                        <p>{item.node.id}</p>
                    </div>
                )
            })}
        </div>
    )
}

export default ScriptPage;
